    },
  ];

  onMount(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            isVisible = true;
            // Delay skill animations
            setTimeout(() => {
              skillsAnimated = true;
            }, 600);
          }
        });
      },
      { threshold: 0.1 }
    );

    const section = document.getElementById("skills");
    if (section) observer.observe(section);

    return () => observer.disconnect();
  });
</script>

<section id="skills" class="py-20 bg-gray-50 dark:bg-gray-800">
  <div class="container mx-auto px-4">
    {#if isVisible}
      <div in:fade={{ duration: 800 }}>
        <!-- Section Header -->
        <div class="text-center mb-16">
          <h2
            in:fly={{ y: 30, duration: 800 }}
            class="text-4xl md:text-5xl font-bold text-gray-900 dark:text-white mb-4"
          >
            My <span class="gradient-text">Skills</span>
          </h2>
          <p
            in:fly={{ y: 30, duration: 800, delay: 200 }}
            class="text-lg text-gray-600 dark:text-gray-400 max-w-2xl mx-auto"
          >
            Technologies and tools I use to bring ideas to life
          </p>
        </div>

        <!-- Skills Grid -->
        <div class="grid md:grid-cols-2 lg:grid-cols-3 gap-8 max-w-6xl mx-auto">
          {#each skillCategories as category, categoryIndex}
            <div
              in:fly={{
                y: 50,
                duration: 800,
                delay: 400 + categoryIndex * 100,
              }}
              class="bg-white dark:bg-gray-900 rounded-xl p-6 shadow-lg hover:shadow-xl transition-all duration-300 group"
            >
              <!-- Category Header -->
              <div class="mb-6">
                <h3
                  class="text-xl font-bold text-gray-900 dark:text-white mb-4"
                >
                  {category.title}
                </h3>
              </div>

              <!-- Skills List -->
              <div class="space-y-6">
                {#each category.skills as skill, skillIndex}
                  <div class="flex items-center space-x-4">
                    <!-- Skill Icon -->
                    <div
                      class="w-10 h-10 p-2 bg-gradient-to-br {skill.color} rounded-lg text-white flex-shrink-0 group-hover:scale-110 transition-transform duration-300"
                    >
                      {@html skill.icon}
                    </div>

                    <!-- Skill Info -->
                    <div class="flex-1">
                      <div class="flex justify-between items-center mb-2">
                        <span
                          class="text-gray-700 dark:text-gray-300 font-medium"
                          >{skill.name}</span
                        >
                        <span class="text-sm text-gray-500 dark:text-gray-400"
                          >{skill.level}%</span
                        >
                      </div>

                      <!-- Progress Bar -->
                      <div
                        class="w-full bg-gray-200 dark:bg-gray-700 rounded-full h-2"
                      >
                        <div
                          class="h-2 rounded-full bg-gradient-to-r {skill.color} transition-all duration-1000 ease-out"
                          style="width: {skillsAnimated ? skill.level : 0}%"
                        />
                      </div>
                    </div>
                  </div>
                {/each}
              </div>
            </div>
          {/each}
        </div>

        <!-- Additional Info -->
        <div
          in:fly={{ y: 50, duration: 800, delay: 1000 }}
          class="mt-16 text-center"
        >
          <div
            class="bg-gradient-to-r from-primary-50 to-secondary-50 dark:from-primary-900/20 dark:to-secondary-900/20 rounded-2xl p-8 max-w-4xl mx-auto"
          >
            <h3 class="text-2xl font-bold text-gray-900 dark:text-white mb-4">
              Always Learning, Always Growing
            </h3>
            <p
              class="text-gray-600 dark:text-gray-400 max-w-3xl mx-auto leading-relaxed mb-8"
            >
              Technology evolves rapidly, and so do I. I'm constantly learning
              new technologies, experimenting with cutting-edge tools, and
              staying updated with industry best practices. My passion for
              continuous learning ensures that I deliver modern, efficient
              solutions.
            </p>

            <!-- Learning Stats -->
            <div class="grid grid-cols-1 md:grid-cols-3 gap-6">
              <div class="text-center">
                <div
                  class="text-3xl font-bold text-primary-600 dark:text-primary-400 mb-2"
                >
                  15+
                </div>
                <div class="text-gray-600 dark:text-gray-400">
                  Technologies Mastered
                </div>
              </div>
              <div class="text-center">
                <div
                  class="text-3xl font-bold text-secondary-600 dark:text-secondary-400 mb-2"
                >
                  50+
                </div>
                <div class="text-gray-600 dark:text-gray-400">
                  Courses Completed
                </div>
              </div>
              <div class="text-center">
                <div
                  class="text-3xl font-bold text-accent-600 dark:text-accent-400 mb-2"
                >
                  5+
                </div>
                <div class="text-gray-600 dark:text-gray-400">
                  Years Experience
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    {/if}
  </div>
</section>
